mapper.insert:

 	SELECT NOTICE_SEQ.NEXTVAL FROM DUAL

INSERT INTO NOTICE(    NNO 
					      , TITLE 
					      , CONTENT 
					      , WRITER
					      , NIMG
					      , NTHUMBIMG  )
              VALUES(    #{nno}
              		  , #{title}
              		  , #{content}
              		  , #{writer}
              		  , #{nImg}
              		  , #{nThumbImg}  )

mapper.update:
UPDATE NOTICE
   SET TITLE    =   #{title},
   	   CONTENT  =   #{content}
   	   NIMG = #{nImg},
   	   NTHUMBIMG = #{nThumbImg}
 WHERE NNO = #{nno}

UPDATE NOTICE SET
HIT = HIT+1
WHERE NNO = #{nno}
