mapper.insert:

 	SELECT BOARD_SEQ.NEXTVAL FROM DUAL

INSERT INTO BOARD(    BNO 
					      , TITLE 
					      , CONTENT 
					      , WRITER
					      , BIMG
					      , BTHUMBIMG  )
              VALUES(    #{bno}
              		  , #{title}
              		  , #{content}
              		  , #{writer} 
              		  , #{bImg}
              		  , #{bThumbImg} )

mapper.update:
UPDATE BOARD
   SET TITLE    =   #{title},
   	   CONTENT  =   #{content},
   	   BIMG = #{bImg},
   	   BTHUMBIMG = #{bThumbImg}
 WHERE BNO = #{bno}

UPDATE BOARD SET
HIT = HIT+1
WHERE BNO = #{bno}
